import React, { useState } from 'react';
import axios from 'axios';

export default function BookingForm() {
  const [formData, setFormData] = useState({
    name: '',
    service: '',
    date: '',
    address: '',
    contact: '',
  });

  const [status, setStatus] = useState(null); // 'success', 'error', 'submitting'
  const [errors, setErrors] = useState({}); // For form validation errors

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
    // Clear validation error for the current field as user types
    if (errors[e.target.name]) {
      setErrors(prevErrors => ({ ...prevErrors, [e.target.name]: null }));
    }
  };

  const validateForm = () => {
    let newErrors = {};
    const { name, service, date, address, contact } = formData;

    if (!name.trim()) {
      newErrors.name = 'Full Name is required.';
    }
    if (!service) {
      newErrors.service = 'Please select a service.';
    }
    if (!date) {
      newErrors.date = 'Preferred Date is required.';
    } else {
      // Optional: Validate if date is not in the past
      const selectedDate = new Date(date);
      const today = new Date();
      today.setHours(0, 0, 0, 0); // Reset time for accurate comparison
      if (selectedDate < today) {
        newErrors.date = 'Booking date cannot be in the past.';
      }
    }
    if (!address.trim()) {
      newErrors.address = 'Address is required.';
    }
    if (!contact.trim()) {
      newErrors.contact = 'Contact Number is required.';
    } else if (!/^\+?[0-9\s-()]{7,20}$/.test(contact.trim())) {
      // Basic regex for phone number validation (adjust as needed)
      newErrors.contact = 'Please enter a valid contact number.';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0; // Return true if no errors
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!validateForm()) {
      setStatus(null); // Clear any previous success/error messages
      return; // Stop submission if validation fails
    }

    setStatus('submitting');
    try {
      // Using a small delay to simulate network latency for 'submitting' state visibility
      await new Promise(resolve => setTimeout(resolve, 1000));
      await axios.post('http://localhost:5000/api/bookings', formData);
      setStatus('success');
      setFormData({
        name: '',
        service: '',
        date: '',
        address: '',
        contact: '',
      });
      setErrors({}); // Clear errors on successful submission
    } catch (error) {
      console.error('❌ Booking error:', error);
      setStatus('error');
    }
  };

  return (
    <form onSubmit={handleSubmit} noValidate> {/* noValidate prevents default browser validation */}
      <div className="mb-3">
        <label htmlFor="name" className="form-label">Full Name</label>
        <input
          type="text"
          className={`form-control ${errors.name ? 'is-invalid' : ''}`}
          id="name"
          name="name"
          value={formData.name}
          onChange={handleChange}
          required
          placeholder="Enter your full name"
          aria-describedby="nameFeedback"
        />
        {errors.name && <div id="nameFeedback" className="invalid-feedback">{errors.name}</div>}
      </div>

      <div className="mb-3">
        <label htmlFor="service" className="form-label">Service</label>
        <select
          className={`form-select ${errors.service ? 'is-invalid' : ''}`}
          id="service"
          name="service"
          value={formData.service}
          onChange={handleChange}
          required
          aria-describedby="serviceFeedback"
        >
          <option value="">Select a service</option>
          <option value="1 Room - $15">1 Room - $15</option>
          <option value="Whole House - $50">Whole House - $50</option>
          <option value="Office - $65">Office - $65</option>
          <option value="Room (Carpets) - $20">Room (Carpets) - $20</option>
          <option value="House (Carpets) - $58">House (Carpets) - $58</option>
          <option value="Office (Carpets) - $72">Office (Carpets) - $72</option>
        </select>
        {errors.service && <div id="serviceFeedback" className="invalid-feedback">{errors.service}</div>}
      </div>

      <div className="mb-3">
        <label htmlFor="date" className="form-label">Preferred Date</label>
        <input
          type="date"
          className={`form-control ${errors.date ? 'is-invalid' : ''}`}
          id="date"
          name="date"
          value={formData.date}
          onChange={handleChange}
          required
          min={new Date().toISOString().split('T')[0]} // Sets minimum date to today
          aria-describedby="dateFeedback"
        />
        {errors.date && <div id="dateFeedback" className="invalid-feedback">{errors.date}</div>}
      </div>

      <div className="mb-3">
        <label htmlFor="address" className="form-label">Address</label>
        <textarea
          className={`form-control ${errors.address ? 'is-invalid' : ''}`}
          id="address"
          name="address"
          value={formData.address}
          onChange={handleChange}
          required
          rows="3"
          placeholder="Enter full address"
          aria-describedby="addressFeedback"
        ></textarea>
        {errors.address && <div id="addressFeedback" className="invalid-feedback">{errors.address}</div>}
      </div>

      <div className="mb-4">
        <label htmlFor="contact" className="form-label">Contact Number</label>
        <input
          type="tel"
          className={`form-control ${errors.contact ? 'is-invalid' : ''}`}
          id="contact"
          name="contact"
          value={formData.contact}
          onChange={handleChange}
          required
          placeholder="e.g. +123456789 or 03XX-XXXXXXX"
          aria-describedby="contactFeedback"
        />
        {errors.contact && <div id="contactFeedback" className="invalid-feedback">{errors.contact}</div>}
      </div>

      <button
        type="submit"
        className="btn btn-primary w-100"
        disabled={status === 'submitting'}
      >
        {status === 'submitting' ? (
          <>
            <span className="spinner-border spinner-border-sm" role="status" aria-hidden="true"></span>
            {' '}Submitting...
          </>
        ) : (
          'Confirm Booking'
        )}
      </button>

      {status === 'success' && (
        <div className="alert alert-success mt-3 text-center" role="alert">
          ✅ Booking successful! We'll be in touch shortly.
        </div>
      )}
      {status === 'error' && (
        <div className="alert alert-danger mt-3 text-center" role="alert">
          ❌ Booking failed. Please check your details and try again.
        </div>
      )}
    </form>
  );
}
