import React from 'react';

export default function Services() {
  const services = [
    {
      name: "Home Cleaning",
      image: "https://prolinerangehoods.com/cdn/shop/articles/whats-included-in-a-professional-house-cleaning-checklist-included-343704.jpg?v=1719132299"
    },
    {
      name: "Office Cleaning",
      image: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTHCtwjnLAk6MUIqUskrx2aIbsbphu3iFA3ZQ&s"
    },
    {
      name: "Carpet Cleaning",
      image: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTlNUB5GCfCiN9QzJsZs6vwYyYty9nIZNZMzw&s"
    }
  ];

  return (
    <div className="container my-5">
      <h2 className="text-center mb-4 display-5 fw-bold">Our Services</h2>
      <div className="row">
        {services.map((s, idx) => (
          <div key={idx} className="col-md-4 mb-4">
            <div className="card shadow-sm h-100">
              <img
                src={s.image}
                className="card-img-top"
                alt={s.name}
                style={{ height: '220px', objectFit: 'cover' }}
              />
              <div className="card-body">
                <h5 className="card-title text-danger fs-4 fw-bold">{s.name}</h5>
                <p className="card-text">
                  Professional {s.name.toLowerCase()} at your convenience.
                </p>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
