// src/components/BookingForm.js
import React, { useState } from 'react';
import axios from 'axios';

const BookingForm = () => {
  const [formData, setFormData] = useState({
    name: '',
    service: '',
    date: ''
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:5000/api/bookings', formData);
      alert('Booking successful!');
       if (onBookingCreated) onBookingCreated(); 
    } catch (error) {
      console.error('Submission error:', error.message);
      alert('Booking failed.');
    }
  };

  return (
    <form onSubmit={handleSubmit} className="p-6 bg-white shadow-lg rounded">
      <input name="name" value={formData.name} onChange={handleChange} placeholder="Name" className="input mb-2" />
      <input name="service" value={formData.service} onChange={handleChange} placeholder="Service" className="input mb-2" />
      <input name="date" type="date" value={formData.date} onChange={handleChange} className="input mb-4" />
      <button type="submit" className="bg-blue-600 text-white px-4 py-2 rounded">Book</button>
    </form>
  );
};

export default BookingForm;
