import React from 'react';

export default function Services() {
  const services = [
    { name: "Home Cleaning", image: "https://prolinerangehoods.com/cdn/shop/articles/whats-included-in-a-professional-house-cleaning-checklist-included-343704.jpg?v=1719132299" },
    { name: "Office Cleaning", image: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTHCtwjnLAk6MUIqUskrx2aIbsbphu3iFA3ZQ&s" },
    { name: "Carpet Cleaning", image: "https://cdn.pixabay.com/photo/2017/06/29/02/35/vacuum-cleaner-2456047_1280.jpg" },
  ];

  return (
    <div className="container my-5">
      <h2 className="text-center mb-4">Our Services</h2>
      <div className="row">
        {services.map((s, idx) => (
          <div key={idx} className="col-md-4 mb-4">
            <div className="card shadow-sm">
              <img src={s.image} className="card-img-top" alt={s.name} />
              <div className="card-body">
                <h5 className="card-title">{s.name}</h5>
                <p className="card-text">Professional {s.name.toLowerCase()} at your convenience.</p>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
