import React from 'react';

export default function About() {
  return (
    <div className="container py-5">
      <div className="text-center mb-5">
        <h2 className="fw-bold text-primary display-5">About CleanSwift</h2>
        <p className="lead text-muted mt-3">
          We make your home and office shine – naturally.
        </p>
      </div>

      <div className="row align-items-center mb-5">
        <div className="col-lg-6 mb-4 mb-lg-0">
          <p className="fs-5">
            <strong>CleanSwift</strong> is a team of experienced professionals passionate about cleanliness and health. We specialize in <span className="text-primary fw-semibold">eco-friendly</span> solutions for both homes and businesses. Our mission is to make every space fresh, hygienic, and welcoming.
          </p>
          <ul className="list-unstyled mt-4">
            <li className="mb-2">✅ 100% Satisfaction Guarantee</li>
            <li className="mb-2">✅ Trusted & Vetted Staff</li>
            <li className="mb-2">✅ Flexible Scheduling</li>
            <li className="mb-2">✅ Eco-Friendly Products</li>
          </ul>
        </div>
        <div className="col-lg-6">
          <div className="d-flex justify-content-between gap-3 flex-wrap">
            <img
              src="https://iconcpl.com/wp-content/uploads/2023/12/cleaning-service-main.webp"
              alt="Cleaning Service"
              className="about-img img-fluid rounded shadow-sm"
            />
            <img
              src="https://mariannas.com/wp-content/uploads/2023/01/Office-Cleaning-Services-For-Hybrid-Work-Schedules-1.jpeg"
              alt="Office Cleaning"
              className="about-img img-fluid rounded shadow-sm"
            />
          </div>
        </div>
      </div>

      {/* Optional Styles */}
      <style jsx="true">{`
        .about-img {
          height: 300px;
          width: 60%;
          object-fit: cover;
          transition: transform 0.4s ease, box-shadow 0.3s ease;
        }

        .about-img:hover {
          transform: scale(1.03);
          box-shadow: 0 0 20px rgba(0, 0, 0, 0.15);
        }

        @media (max-width: 768px) {
          .about-img {
            width: 100%;
            height: 150px;
            margin-bottom: 1rem;
          }
        }
      `}</style>
    </div>
  );
}
