import React, { useState } from 'react';
import axios from 'axios';

export default function BookingForm() {
  const [formData, setFormData] = useState({
    name: '',
    service: '',
    date: '',
    address: '',
    contact: ''
  });

  const handleChange = (e) => {
    setFormData(prev => ({
      ...prev,
      [e.target.name]: e.target.value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:5000/api/bookings', formData);
      alert('Booking successful!');
      setFormData({
        name: '',
        service: '',
        date: '',
        address: '',
        contact: ''
      });
    } catch (error) {
      console.error('❌ Booking error:', error);
      alert('Booking failed!');
    }
  };

  return (
    <div className="container my-5 d-flex justify-content-center">
      <div className="card p-4 shadow" style={{ maxWidth: '500px', width: '100%' }}>
        <h4 className="text-center mb-4">Book a Cleaning Service</h4>
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            name="name"
            className="form-control mb-3"
            placeholder="Your Name"
            value={formData.name}
            onChange={handleChange}
            required
          />
          <select
            name="service"
            className="form-control mb-3"
            value={formData.service}
            onChange={handleChange}
            required
          >
            <option value="">Select Service</option>
            <option value="1 Room - $15">1 Room - $15</option>
            <option value="Whole House - $50">Whole House - $50</option>
            <option value="Office - $65">Office - $65</option>
            <option value="Room (Carpets added) - $20">Room (Carpets added) - $20</option>
            <option value="House (Carpets added) - $58">House (Carpets added) - $58</option>
            <option value="Office (Carpets added) - $72">Office (Carpets added) - $72</option>
          </select>
          <input
            type="date"
            name="date"
            className="form-control mb-3"
            value={formData.date}
            onChange={handleChange}
            required
          />
          <input
            type="text"
            name="address"
            className="form-control mb-3"
            placeholder="Address"
            value={formData.address}
            onChange={handleChange}
            required
          />
          <input
            type="tel"
            name="contact"
            className="form-control mb-4"
            placeholder="Contact Number"
            value={formData.contact}
            onChange={handleChange}
            pattern="[0-9]{10}"
            title="Enter 10-digit phone number"
            required
          />
          <button type="submit" className="btn btn-primary w-100">Book Now</button>
        </form>
      </div>
    </div>
  );
}
