import React, { useState } from 'react';
import axios from 'axios';

export default function BookingForm({ onBookingCreated }) {
  const [formData, setFormData] = useState({ name: '', service: '', date: '' });
  const [loading, setLoading] = useState(false);

  const handleChange = e => setFormData(prev => ({ ...prev, [e.target.name]: e.target.value }));

  const handleSubmit = async e => {
    e.preventDefault();
    setLoading(true);
    try {
    const res = await axios.post("http://localhost:5000/api/bookings", formData);

      onBookingCreated();
      alert(res.data.message || 'Booking successful!');
      setFormData({ name: '', service: '', date: '' });
    } catch {
      alert('Booking failed. Please try again.');
    }
    setLoading(false);
  };

  return (
    <form onSubmit={handleSubmit} className="bg-white shadow-lg rounded-lg p-6 w-full max-w-lg mx-auto -mt-20 relative z-10">
      <h2 className="text-2xl font-bold mb-4 text-center">Book a Service</h2>
      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
        <input name="name" value={formData.name} onChange={handleChange} placeholder="Your Name" required className="p-3 border rounded" />
        <select name="service" value={formData.service} onChange={handleChange} required className="p-3 border rounded">
          <option value="">Select Service</option>
          <option value="Cleaning">Cleaning</option>
          <option value="Catering">Catering</option>
          <option value="Event Management">Event Management</option>
        </select>
        <input type="date" name="date" value={formData.date} onChange={handleChange} required className="p-3 border rounded" />
      </div>
      <button type="submit" disabled={loading} className={`mt-4 w-full p-3 rounded ${loading ? 'bg-gray-400' : 'bg-blue-600 hover:bg-blue-700'} text-white font-semibold`}>
        {loading ? 'Booking...' : 'Submit Booking'}
      </button>
    </form>
  );
}
