import React, { useState } from 'react';
import { Modal, Button } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';

export default function Services() {
  const [showModal, setShowModal] = useState(false);
  const [selectedService, setSelectedService] = useState(null);

  const services = [
    {
      name: "Home Cleaning",
      image: "https://prolinerangehoods.com/cdn/shop/articles/whats-included-in-a-professional-house-cleaning-checklist-included-343704.jpg?v=1719132299",
      fee: "$50",
      time: "1.5 - 2 hours",
      description: "Complete home cleaning including dusting, mopping, and sanitizing surfaces."
    },
    {
      name: "Office Cleaning",
      image: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTHCtwjnLAk6MUIqUskrx2aIbsbphu3iFA3ZQ&s",
      fee: "$65",
      time: "2 - 3 hours",
      description: "Detailed cleaning of workspaces, desks, floors, and common areas."
    },
    {
      name: "Carpet Cleaning",
      image: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTlNUB5GCfCiN9QzJsZs6vwYyYty9nIZNZMzw&s",
      fee: "$20 per room",
      time: "45 min - 1 hour",
      description: "Deep carpet cleaning with stain and odor removal."
    },
  ];

  const handleCardClick = (service) => {
    setSelectedService(service);
    setShowModal(true);
  };

  return (
    <div className="container my-5">
      <h2 className="text-center mb-4 fw-bold fs-1">Our Services</h2>
      <div className="row">
        {services.map((s, idx) => (
          <div key={idx} className="col-md-4 mb-4">
            <div
              className="card shadow-sm service-card h-100"
              onClick={() => handleCardClick(s)}
              style={{ cursor: 'pointer', overflow: 'hidden' }}
            >
              <img
                src={s.image}
                className="card-img-top service-img"
                alt={s.name}
              />
              <div className="card-body">
                <h5 className="card-title text-danger fw-bold fs-5">{s.name}</h5>
                <p className="card-text">Professional {s.name.toLowerCase()} at your convenience.</p>
              </div>
            </div>
          </div>
        ))}
      </div>

      {/* Modal */}
      {selectedService && (
        <Modal show={showModal} onHide={() => setShowModal(false)} centered>
          <Modal.Header closeButton>
            <Modal.Title>{selectedService.name}</Modal.Title>
          </Modal.Header>
          <Modal.Body>
            <img
              src={selectedService.image}
              alt={selectedService.name}
              className="img-fluid mb-3"
              style={{ borderRadius: '10px' }}
            />
            <p><strong>Fee:</strong> {selectedService.fee}</p>
            <p><strong>Estimated Time:</strong> {selectedService.time}</p>
            <p>{selectedService.description}</p>
          </Modal.Body>
          <Modal.Footer>
            <Button variant="secondary" onClick={() => setShowModal(false)}>
              Close
            </Button>
          </Modal.Footer>
        </Modal>
      )}

      {/* Hover Zoom Styles */}
      <style jsx="true">{`
        .service-img {
          transition: transform 0.3s ease;
        }

        .service-card:hover .service-img {
          transform: scale(1.05);
        }

        .service-card:hover {
          box-shadow: 0 0 15px rgba(0, 0, 0, 0.2);
          transition: box-shadow 0.3s ease;
        }
      `}</style>
    </div>
  );
}
