function BookingForm({ db, userId, showStatusMessage }) {
  const [formData, setFormData] = useState({
    name: '',
    service: '',
    date: '',
    address: '',
    contact: '',
  });
  const [isSubmitting, setIsSubmitting] = useState(false);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);

    try {
      if (!db || !userId) {
        showStatusMessage('Firebase not initialized. Please try again later.', false);
        setIsSubmitting(false);
        return;
      }

      // Add a new document with a generated ID to the 'bookings' collection
      const bookingsCollectionRef = collection(db, `artifacts/${__app_id}/users/${userId}/bookings`);
      await addDoc(bookingsCollectionRef, {
        ...formData,
        timestamp: serverTimestamp(), // Add a server timestamp
      });

      showStatusMessage('✅ Booking successful! We will contact you shortly.', true);
      setFormData({
        name: '',
        service: '',
        date: '',
        address: '',
        contact: '',
      });
    } catch (error) {
      console.error('❌ Booking error:', error);
      showStatusMessage('❌ Booking failed. Please try again.', false);
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <form onSubmit={handleSubmit} className="space-y-6">
      <div>
        <label htmlFor="name" className="block text-sm font-medium text-gray-700 mb-1">Full Name</label>
        <input
          type="text"
          id="name"
          className="mt-1 block w-full px-4 py-2 border border-gray-300 rounded-md shadow-sm focus:ring-blue-500 focus:border-blue-500 sm:text-sm transition duration-150 ease-in-out"
          name="name"
          value={formData.name}
          onChange={handleChange}
          required
          placeholder="Enter your full name"
        />
      </div>

      <div>
        <label htmlFor="service" className="block text-sm font-medium text-gray-700 mb-1">Service</label>
        <select
          id="service"
          className="mt-1 block w-full px-4 py-2 border border-gray-300 rounded-md shadow-sm focus:ring-blue-500 focus:border-blue-500 sm:text-sm transition duration-150 ease-in-out bg-white"
          name="service"
          value={formData.service}
          onChange={handleChange}
          required
        >
          <option value="">Select a service</option>
          <option value="1 Room - $15">1 Room - $15</option>
          <option value="Whole House - $50">Whole House - $50</option>
          <option value="Office - $65">Office - $65</option>
          <option value="Room (Carpets) - $20">Room (Carpets) - $20</option>
          <option value="House (Carpets) - $58">House (Carpets) - $58</option>
          <option value="Office (Carpets) - $72">Office (Carpets) - $72</option>
        </select>
      </div>

      <div>
        <label htmlFor="date" className="block text-sm font-medium text-gray-700 mb-1">Preferred Date</label>
        <input
          type="date"
          id="date"
          className="mt-1 block w-full px-4 py-2 border border-gray-300 rounded-md shadow-sm focus:ring-blue-500 focus:border-blue-500 sm:text-sm transition duration-150 ease-in-out"
          name="date"
          value={formData.date}
          onChange={handleChange}
          required
        />
      </div>

      <div>
        <label htmlFor="address" className="block text-sm font-medium text-gray-700 mb-1">Address</label>
        <textarea
          id="address"
          className="mt-1 block w-full px-4 py-2 border border-gray-300 rounded-md shadow-sm focus:ring-blue-500 focus:border-blue-500 sm:text-sm transition duration-150 ease-in-out"
          name="address"
          value={formData.address}
          onChange={handleChange}
          required
          placeholder="Enter full address"
          rows="3"
        ></textarea>
      </div>

      <div>
        <label htmlFor="contact" className="block text-sm font-medium text-gray-700 mb-1">Contact Number</label>
        <input
          type="tel"
          id="contact"
          className="mt-1 block w-full px-4 py-2 border border-gray-300 rounded-md shadow-sm focus:ring-blue-500 focus:border-blue-500 sm:text-sm transition duration-150 ease-in-out"
          name="contact"
          value={formData.contact}
          onChange={handleChange}
          required
          placeholder="e.g. +123456789"
        />
      </div>

      <button
        type="submit"
        className="w-full flex justify-center py-3 px-4 border border-transparent rounded-md shadow-sm text-lg font-bold text-white bg-blue-600 hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500 transition duration-200 ease-in-out disabled:opacity-50 disabled:cursor-not-allowed"
        disabled={isSubmitting}
      >
        {isSubmitting ? (
          <svg className="animate-spin -ml-1 mr-3 h-5 w-5 text-white" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
            <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
            <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
          </svg>
        ) : (
          'Confirm Booking'
        )}
      </button>
    </form>
  );
}
