// BookingForm.js
import React, { useState } from 'react';
import axios from 'axios';

export default function BookingForm() {
  const [formData, setFormData] = useState({
    name: '',
    service: '',
    date: '',
    address: '',
    contact: '',
  });

  const [status, setStatus] = useState(null);
  const [errors, setErrors] = useState({});

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));

    if (errors[name]) {
      setErrors(prev => ({ ...prev, [name]: null }));
    }
  };

  const validateForm = () => {
    const { name, service, date, address, contact } = formData;
    const newErrors = {};

    if (!name.trim()) newErrors.name = 'Full Name is required.';
    if (!service) newErrors.service = 'Please select a service.';
    if (!date) {
      newErrors.date = 'Preferred Date is required.';
    } else {
      const selected = new Date(date);
      const today = new Date();
      today.setHours(0, 0, 0, 0);
      if (selected < today) newErrors.date = 'Booking date cannot be in the past.';
    }
    if (!address.trim()) newErrors.address = 'Address is required.';
    if (!contact.trim()) {
      newErrors.contact = 'Contact Number is required.';
    } else if (!/^\+?[0-9\s\-()]{7,20}$/.test(contact.trim())) {
      newErrors.contact = 'Please enter a valid contact number.';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!validateForm()) {
      setStatus(null);
      return;
    }

    setStatus('submitting');
    try {
      await new Promise(resolve => setTimeout(resolve, 1000)); // simulate delay
      await axios.post('http://localhost:5000/api/bookings', formData);
      setStatus('success');
      setFormData({
        name: '',
        service: '',
        date: '',
        address: '',
        contact: '',
      });
      setErrors({});
    } catch (error) {
      console.error('Booking error:', error);
      setStatus('error');
    }
  };

  return (
    <form onSubmit={handleSubmit} noValidate>
      {/* Full Name */}
      <div className="mb-3">
        <label htmlFor="name" className="form-label">Full Name</label>
        <input
          type="text"
          id="name"
          name="name"
          className={`form-control ${errors.name ? 'is-invalid' : ''}`}
          value={formData.name}
          onChange={handleChange}
          placeholder="Enter your full name"
        />
        {errors.name && <div className="invalid-feedback">{errors.name}</div>}
      </div>

      {/* Service */}
      <div className="mb-3">
        <label htmlFor="service" className="form-label">Service</label>
        <select
          id="service"
          name="service"
          className={`form-select ${errors.service ? 'is-invalid' : ''}`}
          value={formData.service}
          onChange={handleChange}
        >
          <option value="">Select a service</option>
          <option value="1 Room - $15">1 Room - $15</option>
          <option value="Whole House - $50">Whole House - $50</option>
          <option value="Office - $65">Office - $65</option>
          <option value="Room (Carpets) - $20">Room (Carpets) - $20</option>
          <option value="House (Carpets) - $58">House (Carpets) - $58</option>
          <option value="Office (Carpets) - $72">Office (Carpets) - $72</option>
        </select>
        {errors.service && <div className="invalid-feedback">{errors.service}</div>}
      </div>

      {/* Preferred Date */}
      <div className="mb-3">
        <label htmlFor="date" className="form-label">Preferred Date</label>
        <input
          type="date"
          id="date"
          name="date"
          className={`form-control ${errors.date ? 'is-invalid' : ''}`}
          value={formData.date}
          onChange={handleChange}
          min={new Date().toISOString().split('T')[0]}
        />
        {errors.date && <div className="invalid-feedback">{errors.date}</div>}
      </div>

      {/* Address */}
      <div className="mb-3">
        <label htmlFor="address" className="form-label">Address</label>
        <textarea
          id="address"
          name="address"
          rows="3"
          className={`form-control ${errors.address ? 'is-invalid' : ''}`}
          value={formData.address}
          onChange={handleChange}
          placeholder="Enter full address"
        />
        {errors.address && <div className="invalid-feedback">{errors.address}</div>}
      </div>

      {/* Contact */}
      <div className="mb-4">
        <label htmlFor="contact" className="form-label">Contact Number</label>
        <input
          type="tel"
          id="contact"
          name="contact"
          className={`form-control ${errors.contact ? 'is-invalid' : ''}`}
          value={formData.contact}
          onChange={handleChange}
          placeholder="e.g. +123456789 or 03XX-XXXXXXX"
        />
        {errors.contact && <div className="invalid-feedback">{errors.contact}</div>}
      </div>

      {/* Submit Button */}
      <button
        type="submit"
        className="btn btn-primary w-100"
        disabled={status === 'submitting'}
      >
        {status === 'submitting' ? (
          <>
            <span className="spinner-border spinner-border-sm me-2" role="status" aria-hidden="true"></span>
            Submitting...
          </>
        ) : (
          'Confirm Booking'
        )}
      </button>

      {/* Status Messages */}
      {status === 'success' && (
        <div className="alert alert-success mt-3 text-center">
          ✅ Booking successful! We'll be in touch soon.
        </div>
      )}
      {status === 'error' && (
        <div className="alert alert-danger mt-3 text-center">
          ❌ Booking failed. Please try again later.
        </div>
      )}
    </form>
  );
}
