// src/components/BookingForm.js
import React, { useState } from 'react';
import axios from 'axios';

const BookingForm = ({ onBookingCreated }) => {
  const [formData, setFormData] = useState({
    name: '',
    service: '',
    date: ''
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post('http://localhost:5000/api/bookings', formData);
      alert('✅ Booking successful!');
      if (onBookingCreated) onBookingCreated();
      setFormData({ name: '', service: '', date: '' }); // Reset form
    } catch (error) {
      console.error('❌ Booking error:', error);
      alert('❌ Booking failed. See console for details.');
    }
  };

  return (
    <form onSubmit={handleSubmit} className="p-4 bg-light rounded shadow-sm w-100" style={{ maxWidth: '500px', margin: 'auto' }}>
      <h3 className="mb-4">Book a Cleaning</h3>
      <div className="mb-3">
        <input
          type="text"
          name="name"
          placeholder="Your Name"
          className="form-control"
          value={formData.name}
          onChange={handleChange}
          required
        />
      </div>
      <div className="mb-3">
        <input
          type="text"
          name="service"
          placeholder="Service (e.g. Kitchen, Full House)"
          className="form-control"
          value={formData.service}
          onChange={handleChange}
          required
        />
      </div>
      <div className="mb-3">
        <input
          type="date"
          name="date"
          className="form-control"
          value={formData.date}
          onChange={handleChange}
          required
        />
      </div>
      <button type="submit" className="btn btn-primary w-100">Book Now</button>
    </form>
  );
};

export default BookingForm;
