import React, { useState } from 'react';
import axios from 'axios';

const BookingForm = ({ onBookingCreated }) => {
  const [formData, setFormData] = useState({
    name: '',
    service: '',
    date: ''
  });

  const services = [
    "1 Room - $15",
    "Whole House - $50",
    "Office - $65",
    "Room (Carpets Added) - $20",
    "House (Carpets Added) - $58",
    "Office (Carpets Added) - $72"
  ];

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post('http://localhost:5000/api/bookings', formData);
      alert('✅ Booking successful!');
      if (onBookingCreated) onBookingCreated();
      setFormData({ name: '', service: '', date: '' });
    } catch (error) {
      console.error('❌ Booking error:', error);
      alert('❌ Booking failed. See console for details.');
    }
  };

  return (
    <form onSubmit={handleSubmit} className="p-4 bg-light rounded shadow-sm w-100" style={{ maxWidth: '500px', margin: 'auto' }}>
      <h3 className="mb-4 text-center">Book a Cleaning Service</h3>

      {/* Name input */}
      <div className="mb-3">
        <input
          type="text"
          name="name"
          placeholder="Your Name"
          className="form-control"
          value={formData.name}
          onChange={handleChange}
          required
        />
      </div>

      {/* Scrollable dropdown */}
      <div className="mb-3">
        <select
          name="service"
          className="form-select"
          value={formData.service}
          onChange={handleChange}
          required
          size={4} // Shows 4 items with scrollbar
          style={{ maxHeight: '120px', overflowY: 'auto' }}
        >
          <option value="" disabled>Select a service</option>
          {services.map((service, idx) => (
            <option key={idx} value={service}>{service}</option>
          ))}
        </select>
      </div>

      {/* Date input */}
      <div className="mb-3">
        <input
          type="date"
          name="date"
          className="form-control"
          value={formData.date}
          onChange={handleChange}
          required
        />
      </div>

      {/* Submit button */}
      <button type="submit" className="btn btn-primary w-100">Book Now</button>
    </form>
  );
};

export default BookingForm;
