import React, { useState } from 'react';

import axios from 'axios';
export default function BookingForm() {

  const [formData, setFormData] = useState({

    name: '',

    service: '',

    date: '',

    address: '',

    contact: '',

  });



  const [status, setStatus] = useState(null);



  const handleChange = (e) => {

    setFormData({

      ...formData,

      [e.target.name]: e.target.value

    });

  };



  const handleSubmit = async (e) => {

    e.preventDefault();



    try {

      await axios.post('http://localhost:5000/api/bookings', formData);

      setStatus('success');

      setFormData({

        name: '',

        service: '',

        date: '',

        address: '',

        contact: '',

      });

    } catch (error) {

      console.error('❌ Booking error:', error);

      setStatus('error');

    }

  };



  return (

    <form onSubmit={handleSubmit}>

      <div className="mb-3">

        <label className="form-label">Full Name</label>

        <input

          type="text"

          className="form-control"

          name="name"

          value={formData.name}

          onChange={handleChange}

          required

          placeholder="Enter your full name"

        />

      </div>



      <div className="mb-3">

        <label className="form-label">Service</label>

        <select

          className="form-select"

          name="service"

          value={formData.service}

          onChange={handleChange}

          required

        >

          <option value="">Select a service</option>

          <option value="1 Room - $15">1 Room - $15</option>

          <option value="Whole House - $50">Whole House - $50</option>

          <option value="Office - $65">Office - $65</option>

          <option value="Room (Carpets) - $20">Room (Carpets) - $20</option>

          <option value="House (Carpets) - $58">House (Carpets) - $58</option>

          <option value="Office (Carpets) - $72">Office (Carpets) - $72</option>

        </select>

      </div>



      <div className="mb-3">

        <label className="form-label">Preferred Date</label>

        <input

          type="date"

          className="form-control"

          name="date"

          value={formData.date}

          onChange={handleChange}

          required

        />

      </div>



      <div className="mb-3">

        <label className="form-label">Address</label>

        <textarea

          className="form-control"

          name="address"

          value={formData.address}

          onChange={handleChange}

          required

          placeholder="Enter full address"

        ></textarea>

      </div>



      <div className="mb-4">

        <label className="form-label">Contact Number</label>

        <input

          type="tel"

          className="form-control"

          name="contact"

          value={formData.contact}

          onChange={handleChange}

          required

          placeholder="e.g. +123456789"

        />

      </div>



      <button type="submit" className="btn btn-primary w-100">

        Confirm Booking

      </button>



      {status === 'success' && (

        <div className="alert alert-success mt-3 text-center">

          ✅ Booking successful!

        </div>

      )}

      {status === 'error' && (

        <div className="alert alert-danger mt-3 text-center">

          ❌ Booking failed. Please try again.

        </div>

      )}

    </form>

  );

}
